import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import axios from "axios";

const CharacterCard = ({ characters }) => {
  console.log("hasj: ", characters);
  let imagePath = characters.thumbnail.path + ".jpg";

  const handleDetailClick = () => {
    // this.setState(prevState => ({
    //   isToggleOn: !prevState.isToggleOn
    // }));
    const url =
      "https://gateway.marvel.com/v1/public/characters/1011334?apikey=57c07708bb14e463126483f2889b35eb";

    axios
      .get(url)
      .then((response) => {
        console.log("detail response", response);
        // console.log("characters", response.data.data.results);
        // setCharactersList(response.data.data.results);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
    console.log("click");
  };
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        height="140"
        image={imagePath}
        alt="heroes image"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {characters.name}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Share</Button>
        <Button onClick={handleDetailClick} size="small">
          Details
        </Button>
      </CardActions>
    </Card>
  );
};

export default CharacterCard;
