import axios from "axios";
import { useState, useEffect } from "react";
import ComicDetailScreen from "./ComicDetails";

function ComicScreen() {
  const [heroComic, setHeroComic] = useState("");
  const [comicDetails, setComicDetails] = useState("");
  // let imagePath = characters[0].thumbnail.path + ".jpg";
  console.log("setHeroComic: ", heroComic);
  useEffect(() => {
    HeroComic();
  }, []);
  useEffect(() => {
    ComicDetails();
  }, []);

  const HeroComic = () => {
    const url =
      "https://gateway.marvel.com/v1/public/characters/1011334?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";
    axios
      .get(url)
      .then((response) => {
        console.log("detail response", response);
        console.log("hero: ", response.data.data.results[0].comics.items[2]);
        setHeroComic(response.data.data.results[0].comics.items);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
  };

  const ComicDetails = ({ resourceURI }) => {
    const url =
      resourceURI +
      "?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";

    axios
      .get(url)
      .then((response) => {
        console.log("comic response", response);
        // console.log("comic: ", response.data.data.results[0].comics.items[2]);
        // setHeroComic(response.data.data.results[0].comics.items);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
  };

  return (
    <div>
      <h1>Comicss</h1>
      {/* <HeroComic /> */}
      {heroComic.map((heroComics) => (
        <div>
          <h3>{heroComics.name}</h3>
          <h4>{heroComics.resourceURI} </h4>

          {/* <ComicDetails resourceURI={heroComics.resourceURI} /> */}
        </div>
      ))}
    </div>
  );
}

export default ComicScreen;
