import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import axios from "axios";
import { useState, useEffect } from "react";
// import { Link } from "@mui/material";
import { Link, Router } from "react-router-dom";
import ComicScreen from "./Comic";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import TestScreen from "./Test";

const Home = () => (
  <div>
    <h1>Homepage</h1>
  </div>
);

const CharacterCard = () => {
  const [characters, setCharactersList] = useState("");
  console.log("cha:", characters);
  useEffect(() => {
    getCharacters();
  }, []);

  const getCharacters = () => {
    const url =
      "http://gateway.marvel.com/v1/public/characters?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";

    axios
      .get(url)
      .then((response) => {
        console.log("haha response", response);
        console.log("characters", response.data.data.results);
        setCharactersList(response.data.data.results);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
  };

  const [heroComic, setHeroComic] = useState("");

  let imagePath = characters.thumbnail.path + ".jpg";

  const handleHeroDetails = () => {
    // this.setState(prevState => ({
    //   isToggleOn: !prevState.isToggleOn
    // }));
    const url =
      "https://gateway.marvel.com/v1/public/characters/1011334?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";

    axios
      .get(url)
      .then((response) => {
        console.log("detail response", response);
        console.log("hero: ", response.data.data.results[0].comics.items[2]);
        setHeroComic(response.data.data.results[0].comics.items[2]);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });

    return (
      <Router>
        <div>
          <Route path="/" component={Home} />
          <Route path="/Comic" component={ComicScreen} />
        </div>
      </Router>
    );
  };

  return (
    <div>
      {characters.map((characters, index) => (
        <div>
          <CharacterCard characters={characters} key={index} />

          <Card sx={{ maxWidth: 345 }}>
            <CardMedia
              component="img"
              height="140"
              image={imagePath}
              alt="heroes image"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {characters.name}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Share</Button>
              <Button onClick={handleHeroDetails} size="small">
                Details
              </Button>
            </CardActions>
          </Card>
        </div>
      ))}
    </div>
  );

  //   return (

  //     <Card sx={{ maxWidth: 345 }}>
  //       <CardMedia
  //         component="img"
  //         height="140"
  //         image={imagePath}
  //         alt="heroes image"
  //       />
  //       <CardContent>
  //         <Typography gutterBottom variant="h5" component="div">
  //           {characters.name}
  //         </Typography>
  //       </CardContent>
  //       <CardActions>
  //         <Button size="small">Share</Button>
  //         <Button onClick={handleHeroDetails} size="small">
  //           Details
  //         </Button>
  //       </CardActions>
  //     </Card>
  //   );
};

export default CharacterCard;
