import axios from "axios";
import { useState, useEffect, StyleSheet } from "react";
import Card from "@mui/material/Card";

const styles = {
  title: {
    fontSize: "18px",
    color: "blue",
    textAlign: "left",
  },
  body: {
    fontSize: "18px",
    color: "white",
    textAlign: "left",
    border: "1px solid",
    borderColor: "blue",
    padding: "5px",
  },
  bodycontent: {
    border: "1px solid",
    borderColor: "red",
  },
  img: {
    height: "500px",
    margin: "10px",
  },
};
const ComicDetails = ({ comicName, comicURL }) => {
  const [comicDetail, setComicDetail] = useState("");
  useEffect(() => {
    {
      const url =
        comicURL +
        "?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";

      axios
        .get(url)
        .then((response) => {
          console.log("res: ", response);
          setComicDetail(response.data.data.results[0]);
        })
        .catch((error) => {
          console.log("Error marvel: ", error);
        });
    }
  }, []);

  return <ComicCard comicDetail={comicDetail} />;
};

const ComicCard = ({ comicDetail }) => {
  //   console.log("test 2: ", comicDetail);
  let comicCreator = comicDetail.creators.items;
  let comicSeriesName = comicDetail.series.name;
  let comicSaleDate = comicDetail.dates[0].date.split("T")[0];
  let comicTitle = comicDetail.title;
  let comicImage = comicDetail.thumbnail.path + ".jpg";
  let comicDescription = comicDetail.description;
  console.log(comicDescription);

  return (
    <div style={{ margin: "20px", border: "1px solid", borderColor: "red" }}>
      <h1>Comic Detail</h1>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <img src={comicImage} alt="Logo" style={styles.img} />
        <Card
          variant="outlined"
          sx={{ maxWidth: 500 }}
          style={{
            border: "1px solid",
            boxShadow: "0px 0px 6px 3px  grey",
            margin: "10px",
            width: "300px",
            height: "315px",
          }}
        >
          <div style={styles.title}>
            <div style={styles.body}>
              <h2>{comicTitle}</h2>
              <h5>Series: {comicSeriesName}</h5>
              <h5>On Sale Date: {comicSaleDate}</h5>
              <h5>Description: {comicDescription}</h5>
              <h4>Creator: </h4>
              {comicCreator.map((comicCreator, keycomicCreator) => {
                let creatorRole =
                  comicCreator.role.charAt(0).toUpperCase() +
                  comicCreator.role.slice(1);
                return (
                  <div>
                    <h6>
                      {creatorRole}: {comicCreator.name}
                    </h6>
                  </div>
                );
              })}
            </div>
          </div>
        </Card>
      </div>
    </div>
  );
};

const ComicDetailScreen = ({ comicURL }) => {
  return (
    <div>
      <ComicDetails comicURL={comicURL} />;
    </div>
  );
};

export default ComicDetailScreen;
