import axios from "axios";
import { useState, useEffect, StyleSheet } from "react";
// import styles from "./Component.module.css";
const styles = {
  title: {
    fontSize: "18px",
    color: "blue",
    textAlign: "left",
  },
  body: {
    fontSize: "18px",
    color: "white",
    textAlign: "left",
    border: "1px solid",
    borderColor: "blue",
    margin: "10px",
  },
};
const ComicDetails = ({ resourceURI, comicName }) => {
  const [comicCreator, setComicCreator] = useState("");
  useEffect(() => {
    const url = { resourceURI };
    axios
      .get(url.resourceURI)
      .then((response) => {
        console.log(response.data.data.results[0]);
        setComicCreator(response.data.data.results[0].creators.items);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
  }, []);

  return <Test2 comicName={comicName} comicCreator={comicCreator} />;
};

const Test2 = ({ comicName, comicCreator }) => {
  console.log("test 2: ", comicName, comicCreator);
  return (
    <div>
      <h2>ced</h2>
      <div style={styles.title}>
        {comicName.map((comicname) => (
          <div style={styles.body}>
            <h2>{comicname.name}</h2>
          </div>
        ))}
      </div>

      {/* {comicCreator.map((comicDetails) => (
        <div style={styles.body}>
          <p>Creator: {comicDetails.name}</p>
          <p>Role: {comicDetails.role}</p>
        </div>
      ))} */}
    </div>
  );
};

const ComicDetailScreen = ({ resourceURI, comicName }) => {
  return (
    <div>
      <h1>Comic Details</h1>
      <ComicDetails resourceURI={resourceURI} comicName={comicName} />;
    </div>
  );
};

export default ComicDetailScreen;
