import axios from "axios";
import { useState, useEffect, StyleSheet } from "react";
import styles from "./style.css";

const ComicDetails = ({ resourceURI, comicName }) => {
  const [comicDetails, setComicDetails] = useState("");
  useEffect(() => {
    const url = { resourceURI };
    axios
      .get(url.resourceURI)
      .then((response) => {
        setComicDetails(response.data.data.results[0].creators.items);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
  }, []);

  return <Test2 comicName={comicName} comicDetails={comicDetails} />;
};

const Test2 = ({ comicName, comicDetails }) => {
  console.log("test 2: ", comicName, comicDetails);
  return (
    <div>
      <div>
        {comicName.map((comicname) => (
          <div>
            <h2>{comicname.name}</h2>
          </div>
        ))}
      </div>

      {comicDetails.map((comicDetails) => (
        <div className="font-bold">
          <p>{comicDetails.name}</p>
          <p>Role: {comicDetails.role}</p>
        </div>
      ))}
    </div>
  );
};

const ComicDetailScreen = ({ resourceURI, comicName }) => {
  return (
    <div>
      <h1>Comic Details</h1>
      <ComicDetails resourceURI={resourceURI} comicName={comicName} />;
    </div>
  );
};

export default ComicDetailScreen;
