import axios from "axios";
import { useState, useEffect } from "react";
import ComicDetailScreen from "./ComicDetails";
import Button from "@mui/material/Button";
import { useNavigate } from "react-router-dom";
import { Tab, Tabs } from "@mui/material";
import "./App.css";

const styles = {
  title: {
    color: "red",
    padding: "10px",
    margin: "auto",
    textShadow: "2px 1px 3px white",
  },
  tab: {
    justifyItems: "center",
    cursor: "pointer",
  },
  tabs: {
    color: "white",
    fontWeight: "bold",
  },
};

function ComicScreen() {
  const [heroComic, setHeroComic] = useState("");

  const navigate = useNavigate();

  const homeScreen = () => {
    navigate("/home");
  };

  const characterScreen = () => {
    navigate("/characterview");
  };
  useEffect(() => {
    // HeroComic();
    setTimeout(HeroComic(), 10000);
  }, []);

  const HeroComic = () => {
    const url =
      "https://gateway.marvel.com/v1/public/characters/1011334?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";
    axios
      .get(url)
      .then((response) => {
        console.log("detail response: ", response);
        // console.log("hero: ", response.data.data.results[0].comics.items[2]);
        setHeroComic(response.data.data.results[0].comics.items);
      })
      .catch((error) => {
        console.log("Error marvel: ", error);
      });
  };

  return (
    <div>
      <div>
        <header className="header">
          <h1>MARVEL COMIC</h1>
          <Tabs
            className="tab"
            aria-label="basic tabs example"
            style={styles.tab}
          >
            <Tab label="HOME" onClick={homeScreen} style={styles.tabs} />
            <Tab label="HEROES" onClick={characterScreen} style={styles.tabs} />
          </Tabs>
        </header>
      </div>
      {/* {heroComic.map((heroComic, keyHeroComic) => (
        <ComicDetailScreen comicURL={heroComic.resourceURI} />
      ))} */}
    </div>
  );
}

export default ComicScreen;
