import axios from "axios";
import { useState, useEffect, StyleSheet } from "react";
// import styles from "./Component.module.css";
const styles = {
  title: {
    fontSize: "18px",
    color: "blue",
    textAlign: "left",
  },
  body: {
    fontSize: "18px",
    color: "white",
    textAlign: "left",
    border: "1px solid",
    borderColor: "blue",
    padding: "5px",
    margin: "10px",
  },
  bodycontent: {
    border: "1px solid",
    borderColor: "red",
    margin: "10px",
  },
};
const ComicDetails = ({ comicName, comicURL }) => {
  const [comicDetail, setComicDetail] = useState("");
  useEffect(() => {
    {
      // let resourceURI = "http://gateway.marvel.com/v1/public/comics/21366'";
      const url =
        comicURL +
        "?ts=100&apikey=57c07708bb14e463126483f2889b35eb&hash=c41cec366ac6c477f37e5a15e5717be2";

      axios
        .get(url)
        .then((response) => {
          console.log("res: ", response);
          setComicDetail(response.data.data.results[0]);
        })
        .catch((error) => {
          console.log("Error marvel: ", error);
        });
    }
  }, []);

  return <ComicCard comicName={comicName} comicDetail={comicDetail} />;
};

const ComicCard = ({ comicName, comicDetail }) => {
  console.log("test 2: ", comicName, comicDetail);
