import axios from "axios";
import { useState, useEffect, StyleSheet } from "react";
// import styles from "./Component.module.css";
const styles = {
  title: {
    fontSize: "18px",
    color: "blue",
    textAlign: "left",
  },
  body: {
    fontSize: "18px",
    color: "white",
    textAlign: "left",
    border: "1px solid",
    borderColor: "blue",
    padding: "5px",
    margin: "10px",
  },
  bodycontent: {
    border: "1px solid",
    borderColor: "red",
    margin: "10px",
  },
