function Home() {
  return (
    <div style={{ margin: "20px", border: "1px solid", borderColor: "red" }}>
      <h1>Comic Detail</h1>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <img src={comicImage} alt="Logo" style={styles.img} />
        <Card
          variant="outlined"
          sx={{ maxWidth: "500px", maxHeight: "500px" }}
          style={{
            boxShadow: "0px 0px 6px 3px  blue",
            margin: "10px",
            padding: "10px",
            // display: "flex",
            display: "inline-block",
          }}
        >
          <CardActions disableSpacing>
            <div style={styles.title}>
              <Typography>
                {/* <h2>{comicTitle}</h2>
                <h5>Series: {comicSeriesName}</h5>
                <h5>On Sale Date: {comicSaleDate}</h5> */}
                <h4>Description: </h4>
                {/* <h5>{comicDescription}</h5> */}
              </Typography>
              <Accordion>
                <AccordionSummary
                  expandIcon={<ExpandMoreIcon />}
                  aria-controls="panel1a-content"
                  id="panel1a-header"
                >
                  <Typography>More Info</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <ScrollView>
                    <div>
                      <h4>Creator: </h4>
                      {/* {comicCreator.map((comicCreator, keycomicCreator) => {
                        let creatorRole =
                          comicCreator.role.charAt(0).toUpperCase() +
                          comicCreator.role.slice(1);
                        return (
                          <div>
                            <h6>
                              {creatorRole}: {comicCreator.name}
                            </h6>
                          </div>
                        );
                      })} */}
                    </div>
                  </ScrollView>
                </AccordionDetails>
              </Accordion>
            </div>
          </CardActions>
        </Card>
      </div>
    </div>
  );
}

export default Home;
