import axios from "axios";
import { useState, useEffect, StyleSheet } from "react";
import Card from "@mui/material/Card";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import CardActions from "@mui/material/CardActions";
import Typography from "@mui/material/Typography";
import ExpandMore from "@mui/icons-material/ExpandMore";
import { ScrollView } from "@cantonjs/react-scroll-view";
import * as React from "react";
import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";

const styles = {
  title: {
    textAlign: "left",
  },
  body: {
    fontSize: "18px",
    color: "white",
    textAlign: "left",
    padding: "5px",
  },
  bodycontent: {
    border: "1px solid",
    borderColor: "red",
  },
  img: {
    height: "500px",
    margin: "10px",
    boxShadow: "0px 3px 10px 3px  red",
  },
};
function Home() {
  return (
    <div style={{ margin: "20px", border: "1px solid", borderColor: "red" }}>
      <h1>Comic Detail</h1>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        {/* <img src={comicImage} alt="Logo" style={styles.img} /> */}
        <Card
          variant="outlined"
          sx={{ maxWidth: "500px", maxHeight: "500px" }}
          style={{
            boxShadow: "0px 0px 6px 3px  blue",
            margin: "10px",
            padding: "10px",
            // display: "flex",
            display: "inline-block",
          }}
        >
          <CardActions disableSpacing>
            <div style={styles.title}>
              <Typography>
                {/* <h2>{comicTitle}</h2>
                <h5>Series: {comicSeriesName}</h5>
                <h5>On Sale Date: {comicSaleDate}</h5> */}
                <h4>Description: </h4>
                {/* <h5>{comicDescription}</h5> */}
              </Typography>
              <Accordion>
                <AccordionSummary
                  expandIcon={<ExpandMoreIcon />}
                  aria-controls="panel1a-content"
                  id="panel1a-header"
                >
                  <Typography>More Info</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <div style={{ "overflow-y": "scroll" }}>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>
                    <h4>Creator: </h4>

                    {/* {comicCreator.map((comicCreator, keycomicCreator) => {
                        let creatorRole =
                          comicCreator.role.charAt(0).toUpperCase() +
                          comicCreator.role.slice(1);
                        return (
                          <div>
                            <h6>
                              {creatorRole}: {comicCreator.name}
                            </h6>
                          </div>
                        );
                      })} */}
                  </div>
                </AccordionDetails>
              </Accordion>
            </div>
          </CardActions>
        </Card>
      </div>
    </div>
  );
}

export default Home;
